mapper.select:
select 

from mission where mstatus > -1

   and  mission like CONCAT('%',#{qstr,jdbcType=VARCHAR},'%')


   and  startdate >=  #{starttime,jdbcType=BIGINT}


   and  enddate <=  #{endtime,jdbcType=BIGINT}

select 
 count(1) 
from mission where mstatus > -1

   and  mission like CONCAT('%',#{qstr,jdbcType=VARCHAR},'%')


   and  startdate >=  #{starttime,jdbcType=BIGINT}


   and  enddate <=  #{endtime,jdbcType=BIGINT}
