mapper.select:
select

from usermission
where mid = #{mid,jdbcType=BIGINT}
and uid = #{uid,jdbcType=BIGINT}

mapper.delete:
delete from usermission
where mid = #{mid,jdbcType=BIGINT}
and uid = #{uid,jdbcType=BIGINT}

mapper.insert:
insert into usermission (mid, uid, createtime,
updatetime, umstatus, bkid
)
values (#{mid,jdbcType=BIGINT}, #{uid,jdbcType=BIGINT},
#{createtime,jdbcType=BIGINT},
#{updatetime,jdbcType=BIGINT}, #{umstatus,jdbcType=INTEGER}, #{bkid,jdbcType=BIGINT}
)

mapper.update:
update usermission
set createtime = #{createtime,jdbcType=BIGINT},
updatetime = #{updatetime,jdbcType=BIGINT},
umstatus = #{umstatus,jdbcType=INTEGER},
bkid = #{bkid,jdbcType=BIGINT}
where mid = #{mid,jdbcType=BIGINT}
and uid = #{uid,jdbcType=BIGINT}
